        private static void UploadFileToSharePoint(string SiteUrl, string DocLibrary, string ClientSubFolder, string FileName, string Login, string Password)
        {
            try
            {
                #region ConnectToSharePoint
                var securePassword = new SecureString();
                foreach (char c in Password)
                { securePassword.AppendChar(c); }
                var onlineCredentials = new SharePointOnlineCredentials(Login, securePassword);
                #endregion
                #region Insert the data
                using (ClientContext CContext = new ClientContext(SiteUrl))
                {
                    CContext.Credentials = onlineCredentials;
                    Web web = CContext.Web;
                    FileCreationInformation newFile = new FileCreationInformation();
                    //byte[] FileContent = System.IO.File.ReadAllBytes(FileName);


                    var fileContent1 = "";
                    var filename = "house2.jpg";
                    byte[] FileContent0 = System.Convert.FromBase64String(fileContent1);

                    newFile.ContentStream = new MemoryStream(FileContent0);
                    newFile.Url = Path.GetFileName(filename);
                    //newFile.ContentStream = new MemoryStream(FileContent);
                    //newFile.Url = Path.GetFileName(FileName);
                    List DocumentLibrary = web.Lists.GetByTitle(DocLibrary);
                    //SP.Folder folder = DocumentLibrary.RootFolder.Folders.GetByUrl(ClientSubFolder);
                    Folder Clientfolder = DocumentLibrary.RootFolder.Folders.Add(ClientSubFolder);
                    Clientfolder.Update();
                    Microsoft.SharePoint.Client.File uploadFile = Clientfolder.Files.Add(newFile);

                    CContext.Load(DocumentLibrary);
                    CContext.Load(uploadFile,f => f.ListItemAllFields);
                    CContext.ExecuteQuery();
                    var asdf = uploadFile.ListItemAllFields.Id;
                    Console.ForegroundColor = ConsoleColor.Green;
                    Console.WriteLine("The File id is"+ asdf);
                    Console.WriteLine("The File has been uploaded" + Environment.NewLine + "FileUrl -->" + SiteUrl + "/" + DocLibrary + "/" + ClientSubFolder + "/" + Path.GetFileName(filename));
                }
                #endregion
            }
            catch (Exception exp)
            {
                Console.ForegroundColor = ConsoleColor.Red;
                Console.WriteLine(exp.Message + Environment.NewLine + exp.StackTrace);
            }
            finally
            {
                Console.ReadLine();
            }
        }

        public static ClientContext SharepointContext(string SiteUrl, string Login, string Password)
        {
            #region ConnectToSharePoint
            var securePassword = new SecureString();
            foreach (char c in Password)
            { securePassword.AppendChar(c); }
            var onlineCredentials = new SharePointOnlineCredentials(Login, securePassword);
            #endregion

            var context = new ClientContext(SiteUrl);
            context.Credentials = onlineCredentials;
            return context;
        }

        public static void DownloadFile(ClientContext context, string listTitle, int listItemId)
        {
            string downloadPath = @"C:\Users\Anjan\Pictures\FileUploads";

            var list = context.Web.Lists.GetByTitle(listTitle);
            var listItem = list.GetItemById(listItemId);
            context.Load(listItem);
            context.ExecuteQuery();

            var fileRef = (string)listItem["FileRef"];
            var fileLeafRef = (string)listItem["FileLeafRef"];
            var fileInfo = Microsoft.SharePoint.Client.File.OpenBinaryDirect(context, fileRef);

            //var fileDownloadPath = Path.Combine(downloadPath, fileLeafRef);
            //using (var fileStream = System.IO.File.Create(fileDownloadPath))
            //{
            //    fileInfo.Stream.CopyTo(fileStream);
            //}
            byte[] bytes;
            var stream = fileInfo.Stream;
            using(var memoryStream = new MemoryStream())
            {
                stream.CopyTo(memoryStream);
                bytes = memoryStream.ToArray();
            }
            string base64 = Convert.ToBase64String(bytes);
            Console.WriteLine(base64);
            Console.ReadLine();
        }

        public static void DeleteByName(ClientContext context, string listTitle, string filename)
        {
            Web web = context.Web;
            web.ServerRelativeUrl = "/sites/development/";
            var list = context.Web.Lists.GetByTitle(listTitle);

            Microsoft.SharePoint.Client.File f = web.GetFileByServerRelativeUrl(listTitle+"/5fb4ce8faddfee5f50e68da9/"+filename);

            context.Load(f);
            f.DeleteObject();
            context.ExecuteQuery();
            Console.WriteLine("File Deleted");
            Console.ReadLine();
        }
